import React, { useState } from 'react';

const WalmartRewardsApp = () => {
  const [activeModal, setActiveModal] = useState(null);
  const [selectedConversion, setSelectedConversion] = useState(null);

  const customerData = {
    name: "Rajesh Kumar",
    currentTier: "Gold",
    totalPoints: 23450,
    pointsToNextTier: 6550,
    tierProgress: 78
  };

  const tierBenefits = {
    Silver: {
      icon: "🥈",
      color: "text-gray-600",
      bgColor: "bg-gray-100",
      purchaseBonus: "15%",
      recyclingBonus: "1.2x",
      features: ["15% points on purchases", "1.2x bonus on recycling"]
    },
    Gold: {
      icon: "🥇",
      color: "text-yellow-600",
      bgColor: "bg-yellow-100",
      purchaseBonus: "18%",
      recyclingBonus: "1.3x",
      features: ["18% points on purchases", "1.3x recycling bonus", "Faster redemption"]
    },
    Platinum: {
      icon: "💎",
      color: "text-purple-600",
      bgColor: "bg-purple-100",
      purchaseBonus: "20%",
      recyclingBonus: "1.5x",
      features: ["20% points on purchases", "1.5x recycling", "Exclusive partners unlocked"]
    }
  };

  const recentPurchases = [
    { date: "July 6, 2025", amount: 4299, invoiceId: "WAL-INV-34213", points: 645 },
    { date: "June 29, 2025", amount: 1150, invoiceId: "WAL-INV-33782", points: 173 },
    { date: "June 22, 2025", amount: 2990, invoiceId: "WAL-INV-33409", points: 448 }
  ];

  const pointsHistory = [
    { date: "July 6", action: "Purchase", item: "Kitchen Blender", points: 645, bonus: "+15%", type: "purchase" },
    { date: "July 4", action: "Recycle", item: "Old Smartphone", points: 750, bonus: "+30%", type: "recycle" },
    { date: "June 29", action: "Return", item: "Shoes", points: -260, bonus: "(-1.5x rule)", type: "return" },
    { date: "June 25", action: "Purchase", item: "Groceries", points: 312, bonus: "+15%", type: "purchase" }
  ];

  const rewardOptions = [
    {
      id: 'airmiles',
      icon: "✈️",
      title: "Air Miles",
      conversion: "5,000 points → 1,000 miles",
      notes: "Valid on Indigo, Vistara",
      minPoints: 5000,
      bgColor: "bg-blue-50"
    },
    {
      id: 'hotel',
      icon: "🏨",
      title: "Hotel Points",
      conversion: "10,000 points → 2,500 hotel pts",
      notes: "Only on weekdays/off-season",
      minPoints: 10000,
      bgColor: "bg-green-50"
    },
    {
      id: 'coupon',
      icon: "🎟️",
      title: "Walmart Coupon",
      conversion: "2,500 points → ₹250 off",
      notes: "(min cart ₹2,000) Limit: 2 per month",
      minPoints: 2500,
      bgColor: "bg-orange-50"
    }
  ];

  const pointsSummary = {
    shopping: 3200,
    recycling: 1850,
    returns: -390,
    net: 4660
  };

  const handleConversion = (optionId) => {
    const option = rewardOptions.find(o => o.id === optionId);
    if (customerData.totalPoints >= option.minPoints) {
      setSelectedConversion(option);
      setActiveModal('conversion');
    }
  };

  const Modal = ({ title, children, onClose }) => (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-xl max-w-2xl w-full max-h-[90vh] overflow-y-auto shadow-2xl">
        <div className="p-6 border-b border-gray-200 flex justify-between items-center">
          <h2 className="text-xl font-bold text-gray-800">{title}</h2>
          <button 
            onClick={onClose} 
            className="text-gray-500 hover:text-gray-700 text-2xl w-8 h-8 rounded-full hover:bg-gray-100 flex items-center justify-center transition-colors"
          >
            ×
          </button>
        </div>
        <div className="p-6">
          {children}
        </div>
      </div>
    </div>
  );

  const getTypeIcon = (type) => {
    switch(type) {
      case 'purchase': return '🛒';
      case 'recycle': return '♻️';
      case 'return': return '↩️';
      default: return '📄';
    }
  };

  const getTypeColor = (type) => {
    switch(type) {
      case 'purchase': return 'bg-green-100 text-green-600';
      case 'recycle': return 'bg-blue-100 text-blue-600';
      case 'return': return 'bg-red-100 text-red-600';
      default: return 'bg-gray-100 text-gray-600';
    }
  };

  const PointsHistoryModal = () => (
    <div className="space-y-4">
      {pointsHistory.map((entry, index) => (
        <div key={index} className="flex items-center justify-between p-4 bg-gray-50 rounded-lg hover:bg-gray-100 transition-colors">
          <div className="flex items-center space-x-4">
            <div className={`p-2 rounded-full ${getTypeColor(entry.type)}`}>
              <span className="text-lg">{getTypeIcon(entry.type)}</span>
            </div>
            <div>
              <p className="font-medium text-gray-800">{entry.date} - {entry.action}</p>
              <p className="text-sm text-gray-600">{entry.item}</p>
            </div>
          </div>
          <div className="text-right">
            <p className={`font-bold ${entry.points > 0 ? 'text-green-600' : 'text-red-600'}`}>
              {entry.points > 0 ? '+' : ''}{entry.points} pts
            </p>
            <p className="text-xs text-gray-500">{entry.bonus}</p>
          </div>
        </div>
      ))}

      <div className="mt-6 p-4 bg-gradient-to-r from-blue-50 to-purple-50 rounded-lg border border-blue-200">
        <h3 className="font-bold text-blue-800 mb-2">📊 Points Summary</h3>
        <div className="grid grid-cols-2 gap-4 text-sm">
          <div>
            <p className="text-gray-600">Shopping Points: <span className="font-medium text-green-600">+{pointsSummary.shopping} pts</span></p>
            <p className="text-gray-600">Recycling Points: <span className="font-medium text-blue-600">+{pointsSummary.recycling} pts</span></p>
          </div>
          <div>
            <p className="text-gray-600">Return Deductions: <span className="font-medium text-red-600">{pointsSummary.returns} pts</span></p>
            <p className="text-gray-600 font-bold">Net Points: <span className="text-purple-600">{pointsSummary.net} EcoPoints</span></p>
          </div>
        </div>
      </div>
    </div>
  );

  const ConversionModal = () => (
    <div className="space-y-4">
      <div className="p-4 bg-gradient-to-r from-yellow-50 to-orange-50 rounded-lg border border-yellow-200">
        <h3 className="font-bold text-yellow-800 mb-2">🔄 Conversion Details</h3>
        <div className="flex items-center space-x-2 mb-2">
          <span className="text-2xl">{selectedConversion?.icon}</span>
          <div>
            <p className="text-yellow-700 font-medium">{selectedConversion?.title}: {selectedConversion?.conversion}</p>
            <p className="text-sm text-yellow-600">{selectedConversion?.notes}</p>
          </div>
        </div>
      </div>

      <div className="p-4 bg-gray-50 rounded-lg">
        <p className="text-gray-700">Your current balance: <span className="font-bold text-blue-600">{customerData.totalPoints.toLocaleString()} EcoPoints</span></p>
        <p className="text-gray-700">Required points: <span className="font-bold text-orange-600">{selectedConversion?.minPoints.toLocaleString()} EcoPoints</span></p>
        <p className="text-gray-700">Remaining after conversion: <span className="font-bold text-green-600">{(customerData.totalPoints - selectedConversion?.minPoints).toLocaleString()} EcoPoints</span></p>
      </div>

      <div className="flex space-x-4">
        <button 
          onClick={() => setActiveModal(null)}
          className="flex-1 px-4 py-2 border border-gray-300 rounded-lg text-gray-700 hover:bg-gray-50 transition-colors"
        >
          Cancel
        </button>
        <button 
          onClick={() => {
            alert(`Conversion confirmed! Your ${selectedConversion?.title} will be processed within 24 hours.`);
            setActiveModal(null);
          }}
          className="flex-1 px-4 py-2 bg-gradient-to-r from-blue-600 to-purple-600 text-white rounded-lg hover:from-blue-700 hover:to-purple-700 transition-all duration-200 shadow-lg"
        >
          Confirm Conversion
        </button>
      </div>
    </div>
  );

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-purple-50">
      {/* Header */}
      <div className="bg-gradient-to-r from-blue-600 to-purple-600 text-white p-6 shadow-lg">
        <div className="max-w-4xl mx-auto">
          <div className="flex items-center justify-between">
            <div>
              <h1 className="text-3xl font-bold mb-1">🏪 Walmart Rewards+</h1>
              <p className="text-blue-100">Welcome back, {customerData.name}!</p>
            </div>
            <div className="text-right">
              <p className="text-sm text-blue-100">Tier: {tierBenefits[customerData.currentTier].icon} {customerData.currentTier}</p>
              <p className="text-xl font-bold">Total Points: {customerData.totalPoints.toLocaleString()} EcoPoints</p>
            </div>
          </div>
        </div>
      </div>

      <div className="max-w-4xl mx-auto p-6 space-y-6">
        {/* Tier & Points Summary */}
        <div className="bg-white rounded-2xl shadow-lg p-6 border border-gray-100">
          <h2 className="text-xl font-bold text-gray-800 mb-4">💼 Tier & Points Summary</h2>

          <div className="grid md:grid-cols-3 gap-4 mb-6">
            {Object.entries(tierBenefits).map(([tier, details]) => (
              <div key={tier} className={`p-4 rounded-xl border-2 transition-all duration-200 hover:shadow-md ${
                tier === customerData.currentTier 
                  ? 'border-blue-500 bg-gradient-to-br from-blue-50 to-purple-50 shadow-md' 
                  : 'border-gray-200 hover:border-gray-300'
              }`}>
                <div className="flex items-center space-x-2 mb-2">
                  <span className="text-2xl">{details.icon}</span>
                  <h3 className={`font-bold ${details.color}`}>{tier}</h3>
                </div>
                <div className="text-sm text-gray-600 space-y-1">
                  {details.features.map((feature, index) => (
                    <p key={index}>• {feature}</p>
                  ))}
                </div>
              </div>
            ))}
          </div>

          <div className="bg-gradient-to-r from-yellow-50 to-purple-50 p-4 rounded-xl border border-yellow-200">
            <p className="text-gray-700 mb-2">
              You are currently <span className="font-bold text-yellow-600">{customerData.currentTier} Tier</span>. 
              Only <span className="font-bold text-purple-600">{customerData.pointsToNextTier.toLocaleString()} points</span> left to reach Platinum.
            </p>
            <div className="w-full bg-gray-200 rounded-full h-3 mb-2">
              <div 
                className="bg-gradient-to-r from-yellow-500 to-purple-500 h-3 rounded-full transition-all duration-500"
                style={{ width: `${customerData.tierProgress}%` }}
              ></div>
            </div>
            <p className="text-sm text-gray-600">{customerData.tierProgress}% to Platinum</p>
          </div>
        </div>

        {/* Recent Purchases */}
        <div className="bg-white rounded-2xl shadow-lg p-6 border border-gray-100">
          <h2 className="text-xl font-bold text-gray-800 mb-4">📄 Recent Purchases</h2>
          <div className="overflow-x-auto">
            <table className="w-full">
              <thead>
                <tr className="border-b border-gray-200">
                  <th className="text-left py-3 text-gray-600 font-medium">Date</th>
                  <th className="text-left py-3 text-gray-600 font-medium">Amount</th>
                  <th className="text-left py-3 text-gray-600 font-medium">Invoice ID</th>
                  <th className="text-left py-3 text-gray-600 font-medium">Points Earned</th>
                  <th className="text-left py-3 text-gray-600 font-medium">Invoice</th>
                </tr>
              </thead>
              <tbody>
                {recentPurchases.map((purchase, index) => (
                  <tr key={index} className="border-b border-gray-100 hover:bg-gray-50 transition-colors">
                    <td className="py-3 text-gray-800">{purchase.date}</td>
                    <td className="py-3 text-gray-800 font-medium">₹{purchase.amount.toLocaleString()}</td>
                    <td className="py-3 text-gray-600">{purchase.invoiceId}</td>
                    <td className="py-3 text-green-600 font-medium">+{purchase.points} pts</td>
                    <td className="py-3">
                      <button className="text-blue-600 hover:text-blue-800 flex items-center space-x-1 transition-colors">
                        <span className="text-sm">📥</span>
                        <span>PDF</span>
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
          <button 
            onClick={() => setActiveModal('history')}
            className="mt-4 text-blue-600 hover:text-blue-800 flex items-center space-x-1 transition-colors"
          >
            <span>👁️</span>
            <span>View Full Points History</span>
            <span>→</span>
          </button>
        </div>

        {/* Redeem Points */}
        <div className="bg-white rounded-2xl shadow-lg p-6 border border-gray-100">
          <h2 className="text-xl font-bold text-gray-800 mb-4">🎯 Redeem Points</h2>
          <div className="grid md:grid-cols-3 gap-4">
            {rewardOptions.map((option) => (
              <div key={option.id} className={`p-4 rounded-xl border-2 border-gray-200 hover:border-blue-300 transition-all duration-200 hover:shadow-md ${option.bgColor}`}>
                <div className="flex items-center space-x-3 mb-3">
                  <span className="text-2xl">{option.icon}</span>
                  <h3 className="font-bold text-gray-800">{option.title}</h3>
                </div>
                <p className="text-sm text-gray-600 mb-2 font-medium">{option.conversion}</p>
                <p className="text-xs text-gray-500 mb-4">{option.notes}</p>
                <button 
                  onClick={() => handleConversion(option.id)}
                  disabled={customerData.totalPoints < option.minPoints}
                  className={`w-full py-2 px-4 rounded-lg font-medium transition-all duration-200 ${
                    customerData.totalPoints >= option.minPoints 
                      ? 'bg-gradient-to-r from-blue-600 to-purple-600 text-white hover:from-blue-700 hover:to-purple-700 shadow-lg hover:shadow-xl' 
                      : 'bg-gray-300 text-gray-500 cursor-not-allowed'
                  }`}
                >
                  {customerData.totalPoints >= option.minPoints ? 'Convert Points' : 'Insufficient Points'}
                </button>
              </div>
            ))}
          </div>
        </div>

        {/* Points Logic Info */}
        <div className="bg-gradient-to-r from-blue-50 to-purple-50 rounded-2xl p-6 border border-blue-200 shadow-lg">
          <h2 className="text-xl font-bold text-gray-800 mb-4">🧠 Points Logic & Rewards Policy</h2>
          <div className="grid md:grid-cols-2 gap-6">
            <div>
              <h3 className="font-bold text-blue-800 mb-2">How You Earn Points</h3>
              <div className="space-y-2 text-sm">
                <p className="flex items-center space-x-2">
                  <span className="text-green-600">🛒</span>
                  <span><strong>Purchase:</strong> {tierBenefits[customerData.currentTier].purchaseBonus} of MRP → points</span>
                </p>
                <p className="flex items-center space-x-2">
                  <span className="text-blue-600">♻️</span>
                  <span><strong>Recycle:</strong> 50% of estimated value ({customerData.currentTier} adds 30% bonus)</span>
                </p>
                <p className="flex items-center space-x-2">
                  <span className="text-red-600">↩️</span>
                  <span><strong>Returns:</strong> Deduct 1.5× original points</span>
                </p>
              </div>
            </div>
            <div>
              <h3 className="font-bold text-purple-800 mb-2">Redemption Features</h3>
              <div className="space-y-2 text-sm">
                <p><strong>✈️ Air Miles:</strong> Capped + co-funded with airline partners</p>
                <p><strong>🏨 Hotels:</strong> Off-season promotion only for better margins</p>
                <p><strong>🎟️ Coupons:</strong> Minimum spend conditions to drive sales</p>
                <p className="text-xs text-gray-600 mt-3">
                  ⚠️ All rewards designed for high engagement & controlled cost impact
                </p>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Modals */}
      {activeModal === 'history' && (
        <Modal title="📜 Full Points History" onClose={() => setActiveModal(null)}>
          <PointsHistoryModal />
        </Modal>
      )}

      {activeModal === 'conversion' && (
        <Modal title="✅ Convert Points" onClose={() => setActiveModal(null)}>
          <ConversionModal />
        </Modal>
      )}
    </div>
  );
};

export default WalmartRewardsApp;
