import React, { useState } from 'react';

const WalmartRewardsAdmin = () => {
  const [activeModal, setActiveModal] = useState(null);
  const [purchaseAmount, setPurchaseAmount] = useState('');
  const [selectedTier, setSelectedTier] = useState('Gold');
  const [customerId, setCustomerId] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [selectedCustomer, setSelectedCustomer] = useState(null);

  // Sample customer data
  const sampleCustomers = [
    {
      id: "CUST1001",
      name: "Rajesh Kumar",
      currentTier: "Gold",
      totalPoints: 23450,
      phone: "+919876543210",
      email: "rajesh.kumar@example.com",
      lastPurchase: "2025-07-06"
    },
    {
      id: "CUST1002",
      name: "Priya Sharma",
      currentTier: "Silver",
      totalPoints: 12500,
      phone: "+919876543211",
      email: "priya.sharma@example.com",
      lastPurchase: "2025-07-05"
    },
    {
      id: "CUST1003",
      name: "Amit Patel",
      currentTier: "Platinum",
      totalPoints: 45600,
      phone: "+919876543212",
      email: "amit.patel@example.com",
      lastPurchase: "2025-07-07"
    }
  ];

  const tierBenefits = {
    Silver: {
      icon: "ðŸ¥ˆ",
      color: "text-gray-600",
      bgColor: "bg-gray-100",
      purchaseRate: 0.15,
      features: ["15% points on purchases", "1.2x bonus on recycling"]
    },
    Gold: {
      icon: "ðŸ¥‡",
      color: "text-yellow-600",
      bgColor: "bg-yellow-100",
      purchaseRate: 0.18,
      features: ["18% points on purchases", "1.3x recycling bonus", "Faster redemption"]
    },
    Platinum: {
      icon: "ðŸ’Ž",
      color: "text-purple-600",
      bgColor: "bg-purple-100",
      purchaseRate: 0.20,
      features: ["20% points on purchases", "1.5x recycling", "Exclusive partners unlocked"]
    }
  };

  const recentTransactions = [
    { 
      id: "TXN78901",
      customerId: "CUST1001",
      customerName: "Rajesh Kumar",
      date: "2025-07-06 14:30", 
      amount: 4299, 
      points: 645,
      tier: "Gold",
      cashier: "Admin001"
    },
    { 
      id: "TXN78902",
      customerId: "CUST1002",
      customerName: "Priya Sharma",
      date: "2025-07-05 11:15", 
      amount: 1150, 
      points: 173,
      tier: "Silver",
      cashier: "Admin002"
    },
    { 
      id: "TXN78903",
      customerId: "CUST1003",
      customerName: "Amit Patel",
      date: "2025-07-07 16:45", 
      amount: 2990, 
      points: 598,
      tier: "Platinum",
      cashier: "Admin001"
    },
    { 
      id: "TXN78904",
      customerId: "CUST1001",
      customerName: "Rajesh Kumar",
      date: "2025-06-29 09:20", 
      amount: 1150, 
      points: 173,
      tier: "Gold",
      cashier: "Admin003"
    }
  ];

  const handleSearch = (e) => {
    e.preventDefault();
    if (customerId.trim() === '') {
      setSearchResults([]);
      return;
    }

    const results = sampleCustomers.filter(customer => 
      customer.id.includes(customerId) || 
      customer.name.toLowerCase().includes(customerId.toLowerCase()) ||
      customer.phone.includes(customerId) ||
      customer.email.toLowerCase().includes(customerId.toLowerCase())
    );

    setSearchResults(results);
  };

  const calculatePoints = (amount, tier) => {
    const rate = tierBenefits[tier].purchaseRate;
    return Math.floor(amount * rate);
  };

  const handleAddPoints = (e) => {
    e.preventDefault();
    if (!selectedCustomer || !purchaseAmount || isNaN(purchaseAmount) || purchaseAmount <= 0) {
      alert('Please select a customer and enter a valid amount');
      return;
    }

    const points = calculatePoints(parseFloat(purchaseAmount), selectedTier);
    const newTransaction = {
      id: `TXN${Math.floor(Math.random() * 100000)}`,
      customerId: selectedCustomer.id,
      customerName: selectedCustomer.name,
      date: new Date().toISOString(),
      amount: parseFloat(purchaseAmount),
      points: points,
      tier: selectedTier,
      cashier: "Admin001" // This would be the logged in admin in a real app
    };

    // In a real app, you would send this to your backend
    console.log('Adding points:', newTransaction);
    alert(`Successfully added ${points} points to ${selectedCustomer.name}'s account for purchase of â‚¹${purchaseAmount}`);

    // Reset form
    setPurchaseAmount('');
    setActiveModal(null);
    setSelectedCustomer(null);
  };

  const Modal = ({ title, children, onClose }) => (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-xl max-w-2xl w-full max-h-[90vh] overflow-y-auto shadow-2xl">
        <div className="p-6 border-b border-gray-200 flex justify-between items-center">
          <h2 className="text-xl font-bold text-gray-800">{title}</h2>
          <button 
            onClick={onClose} 
            className="text-gray-500 hover:text-gray-700 text-2xl w-8 h-8 rounded-full hover:bg-gray-100 flex items-center justify-center transition-colors"
          >
            Ã—
          </button>
        </div>
        <div className="p-6">
          {children}
        </div>
      </div>
    </div>
  );

  const AddPointsModal = () => (
    <form onSubmit={handleAddPoints}>
      <div className="space-y-6">
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-1">Selected Customer</label>
          {selectedCustomer ? (
            <div className="p-3 bg-gray-50 rounded-lg border border-gray-200">
              <div className="flex justify-between items-center">
                <div>
                  <p className="font-medium">{selectedCustomer.name}</p>
                  <p className="text-sm text-gray-600">ID: {selectedCustomer.id}</p>
                </div>
                <div className="text-right">
                  <p className="text-sm">
                    Tier: <span className={`font-medium ${tierBenefits[selectedCustomer.currentTier].color}`}>
                      {selectedCustomer.currentTier}
                    </span>
                  </p>
                  <p className="text-sm">Points: {selectedCustomer.totalPoints.toLocaleString()}</p>
                </div>
              </div>
            </div>
          ) : (
            <p className="text-sm text-gray-500">No customer selected</p>
          )}
        </div>

        <div>
          <label htmlFor="purchaseAmount" className="block text-sm font-medium text-gray-700 mb-1">
            Purchase Amount (â‚¹)
          </label>
          <input
            type="number"
            id="purchaseAmount"
            value={purchaseAmount}
            onChange={(e) => setPurchaseAmount(e.target.value)}
            className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
            placeholder="Enter purchase amount"
            min="1"
            step="1"
            required
          />
        </div>

