import React, { useState, useRef, useEffect } from 'react';

const WalmartRewardsAdmin = () => {
  // State management
  const [state, setState] = useState({
    activeModal: null,
    purchaseAmount: '',
    selectedTier: 'Gold',
    searchQuery: '',
    searchResults: [],
    selectedCustomer: null,
    showProfileMenu: false,
    transactionStatus: null,
    notification: null
  });

  // Refs
  const profileMenuRef = useRef();
  const modalRef = useRef();

  // Sample data
  const sampleCustomers = [
    {
      id: "CUST1001",
      name: "Rajesh Kumar",
      currentTier: "Gold",
      totalPoints: 23450,
      phone: "+919876543210",
      email: "rajesh.kumar@example.com",
      lastPurchase: "2025-07-06",
      joinDate: "2023-05-15"
    },
    {
      id: "CUST1002",
      name: "Priya Sharma",
      currentTier: "Silver",
      totalPoints: 12500,
      phone: "+919876543211",
      email: "priya.sharma@example.com",
      lastPurchase: "2025-07-05",
      joinDate: "2024-01-22"
    },
    {
      id: "CUST1003",
      name: "Amit Patel",
      currentTier: "Platinum",
      totalPoints: 45600,
      phone: "+919876543212",
      email: "amit.patel@example.com",
      lastPurchase: "2025-07-07",
      joinDate: "2022-11-30"
    }
  ];

  const tierBenefits = {
    Silver: {
      icon: "🥈",
      color: "text-gray-600",
      bgColor: "bg-gray-100",
      purchaseRate: 0.15,
      features: ["15% points on purchases", "1.2x bonus on recycling"]
    },
    Gold: {
      icon: "🥇",
      color: "text-yellow-600",
      bgColor: "bg-yellow-100",
      purchaseRate: 0.18,
      features: ["18% points on purchases", "1.3x recycling bonus", "Faster redemption"]
    },
    Platinum: {
      icon: "💎",
      color: "text-purple-600",
      bgColor: "bg-purple-100",
      purchaseRate: 0.20,
      features: ["20% points on purchases", "1.5x recycling", "Exclusive partners unlocked"]
    }
  };

  const recentTransactions = [
    { 
      id: "TXN78901",
      customerId: "CUST1001",
      customerName: "Rajesh Kumar",
      date: "2025-07-06 14:30", 
      amount: 4299, 
      points: 645,
      tier: "Gold",
      cashier: "Admin001"
    },
    { 
      id: "TXN78902",
      customerId: "CUST1002",
      customerName: "Priya Sharma",
      date: "2025-07-05 11:15", 
      amount: 1150, 
      points: 173,
      tier: "Silver",
      cashier: "Admin002"
    },
    { 
      id: "TXN78903",
      customerId: "CUST1003",
      customerName: "Amit Patel",
      date: "2025-07-07 16:45", 
      amount: 2990, 
      points: 598,
      tier: "Platinum",
      cashier: "Admin001"
    },
    { 
      id: "TXN78904",
      customerId: "CUST1001",
      customerName: "Rajesh Kumar",
      date: "2025-06-29 09:20", 
      amount: 1150, 
      points: 173,
      tier: "Gold",
      cashier: "Admin003"
    }
  ];

  // Click outside handler for profile menu
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (profileMenuRef.current && !profileMenuRef.current.contains(event.target)) {
        setState(prev => ({ ...prev, showProfileMenu: false }));
      }
    };
    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  // Handle search
  const handleSearch = (e) => {
    e.preventDefault();
    const query = state.searchQuery.trim().toLowerCase();

    if (!query) {
      setState(prev => ({ ...prev, searchResults: [] }));
      return;
    }

    const results = sampleCustomers.filter(customer => 
      customer.id.toLowerCase().includes(query) || 
      customer.name.toLowerCase().includes(query) ||
      customer.phone.includes(query) ||
      customer.email.toLowerCase().includes(query)
    );

    setState(prev => ({ ...prev, searchResults: results }));
  };

  // Calculate points
  const calculatePoints = (amount, tier) => {
    const rate = tierBenefits[tier].purchaseRate;
    return Math.floor(amount * rate);
  };

  // Handle adding points
  const handleAddPoints = (e) => {
    e.preventDefault();

    if (!state.selectedCustomer || !state.purchaseAmount || isNaN(state.purchaseAmount) || parseFloat(state.purchaseAmount) <= 0) {
      setState(prev => ({
        ...prev,
        notification: {
          type: 'error',
          message: 'Please enter a valid purchase amount'
        }
      }));
      setTimeout(() => setState(prev => ({ ...prev, notification: null })), 3000);
      return;
    }

    const amount = parseFloat(state.purchaseAmount);
    const points = calculatePoints(amount, state.selectedTier);

    // Simulate API call
    setState(prev => ({ ...prev, transactionStatus: 'processing' }));

    setTimeout(() => {
      setState(prev => ({
        ...prev,
        transactionStatus: 'success',
        notification: {
          type: 'success',
          message: `${points} points added successfully!`
        },
        purchaseAmount: '',
        activeModal: null
      }));

      // Hide success message after 3 seconds
      setTimeout(() => {
        setState(prev => ({ ...prev, transactionStatus: null }));
      }, 3000);
    }, 1500);
  };

  // Format currency
  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR',
      minimumFractionDigits: 0
    }).format(amount);
  };

  // Modal component
  const Modal = ({ title, children, onClose }) => (
    React.createElement('div', {
      className: "fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4",
      onClick: (e) => e.target === e.currentTarget && onClose()
    },
      React.createElement('div', {
        className: "bg-white rounded-xl max-w-2xl w-full max-h-[90vh] overflow-y-auto shadow-2xl",
        ref: modalRef
      },
        React.createElement('div', { className: "p-6 border-b border-gray-200 flex justify-between items-center" },
          React.createElement('h2', { className: "text-xl font-bold text-gray-800" }, title),
          React.createElement('button', {
            onClick: onClose,
            className: "text-gray-500 hover:text-gray-700 text-2xl w-8 h-8 rounded-full hover:bg-gray-100 flex items-center justify-center transition-colors"
          }, '×')
        ),
        React.createElement('div', { className: "p-6" }, children)
      )
    )
  );

  // Add Points Modal
  const AddPointsModal = () => (
    React.createElement('form', { onSubmit: handleAddPoints },
      React.createElement('div', { className: "space-y-6" },
        React.createElement('div', null,
          React.createElement('label', { className: "block text-sm font-medium text-gray-700 mb-1" }, "Selected Customer"),
          state.selectedCustomer ? (
            React.createElement('div', { className: "p-3 bg-gray-50 rounded-lg border border-gray-200" },
              React.createElement('div', { className: "flex justify-between items-center" },
                React.createElement('div', null,
                  React.createElement('p', { className: "font-medium" }, state.selectedCustomer.name),
                  React.createElement('p', { className: "text-sm text-gray-600" }, `ID: ${state.selectedCustomer.id}`)
                ),
                React.createElement('div', { className: "text-right" },
                  React.createElement('p', { className: "text-sm" },
                    "Tier: ",
                    React.createElement('span', { className: `font-medium ${tierBenefits[state.selectedCustomer.currentTier].color}` },
                      state.selectedCustomer.currentTier
                    )
                  ),
                  React.createElement('p', { className: "text-sm" }, `${state.selectedCustomer.totalPoints.toLocaleString()} pts`)
                )
              )
            )
          ) : (
            React.createElement('p', { className: "text-sm text-gray-500" }, "No customer selected")
          )
        ),

        React.createElement('div', null,
          React.createElement('label', { 
            htmlFor: "purchaseAmount", 
            className: "block text-sm font-medium text-gray-700 mb-1" 
          }, "Purchase Amount (₹)"),
          React.createElement('input', {
            type: "text",
            inputMode: "decimal",
            id: "purchaseAmount",
            value: state.purchaseAmount,
            onChange: (e) => {
              const value = e.target.value.replace(/[^0-9.]/g, '');
              if (value === '' || /^\d*\.?\d*$/.test(value)) {
                setState(prev => ({ ...prev, purchaseAmount: value }));
              }
            },
            className: "w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500",
            placeholder: "Enter purchase amount",
            required
          })
        ),

        React.createElement('div', null,
          React.createElement('label', { className: "block text-sm font-medium text-gray-700 mb-1" }, "Customer Tier"),
          React.createElement('div', { className: "grid grid-cols-3 gap-2" },
            Object.keys(tierBenefits).map((tier) => (
              React.createElement('button', {
                key: tier,
                type: "button",
                onClick: () => setState(prev => ({ ...prev, selectedTier: tier })),
                className: `p-2 rounded-lg border-2 transition-all ${
                  state.selectedTier === tier 
                    ? 'border-blue-500 bg-blue-50' 
                    : 'border-gray-200 hover:border-gray-300'
                }`
              },
                React.createElement('div', { className: "flex items-center justify-center space-x-1" },
                  React.createElement('span', null, tierBenefits[tier].icon),
                  React.createElement('span', null, tier)
                )
              )
            ))
          )
        ),

        state.purchaseAmount && !isNaN(state.purchaseAmount) && parseFloat(state.purchaseAmount) > 0 && (
          React.createElement('div', { className: "p-4 bg-blue-50 rounded-lg border border-blue-200" },
            React.createElement('p', { className: "font-medium text-blue-800" }, "Points Calculation"),
            React.createElement('p', { className: "text-sm" },
              `${formatCurrency(parseFloat(state.purchaseAmount))} × ${tierBenefits[state.selectedTier].purchaseRate * 100}% = `,
              React.createElement('span', { className: "font-bold" },
                ` ${calculatePoints(parseFloat(state.purchaseAmount), state.selectedTier).toLocaleString()} points`
              )
            )
          )
        ),

        state.transactionStatus === 'processing' ? (
          React.createElement('div', { className: "flex justify-center py-4" },
            React.createElement('div', { className: "animate-spin rounded-full h-8 w-8 border-t-2 border-b-2 border-blue-500" })
          )
        ) : (
          React.createElement('div', { className: "flex space-x-4 pt-4" },
            React.createElement('button', {
              type: "button",
              onClick: () => setState(prev => ({ ...prev, activeModal: null })),
              className: "flex-1 px-4 py-2 border border-gray-300 rounded-lg text-gray-700 hover:bg-gray-50 transition-colors"
            }, "Cancel"),
            React.createElement('button', {
              type: "submit",
              className: "flex-1 px-4 py-2 bg-gradient-to-r from-blue-600 to-purple-600 text-white rounded-lg hover:from-blue-700 hover:to-purple-700 transition-all duration-200 shadow-lg hover:shadow-xl"
            }, "Add Points")
          )
        )
      )
    )
  );

  // Success Notification
  const SuccessNotification = () => (
    React.createElement('div', { 
      className: "fixed bottom-6 right-6 z-50 animate-fade-in-up"
    },
      React.createElement('div', { 
        className: "bg-green-100 border-l-4 border-green-500 text-green-700 p-4 shadow-lg rounded-lg flex items-center"
      },
        React.createElement('svg', { 
          className: "h-6 w-6 text-green-500 mr-4", 
          xmlns: "http://www.w3.org/2000/svg", 
          viewBox: "0 0 20 20", 
          fill: "currentColor"
        },
          React.createElement('path', { 
            fillRule: "evenodd", 
            d: "M10 18a8 8 0 100-16 8 8 0 000 16zm3.707-9.293a1 1 0 00-1.414-1.414L9 10.586 7.707 9.293a1 1 0 00-1.414 1.414l2 2a1 1 0 001.414 0l4-4z", 
            clipRule: "evenodd" 
          })
        ),
        React.createElement('div', null,
          React.createElement('p', { className: "font-bold" }, "Transaction Successful"),
          React.createElement('p', null, state.notification.message)
        )
      )
    )
  );

  // Error Notification
  const ErrorNotification = () => (
    React.createElement('div', { 
      className: "fixed bottom-6 right-6 z-50 animate-fade-in-up"
    },
      React.createElement('div', { 
        className: "bg-red-100 border-l-4 border-red-500 text-red-700 p-4 shadow-lg rounded-lg flex items-center"
      },
        React.createElement('svg', { 
          className: "h-6 w-6 text-red-500 mr-4", 
          xmlns: "http://www.w3.org/2000/svg", 
          viewBox: "0 0 20 20", 
          fill: "currentColor"
        },
          React.createElement('path', { 
            fillRule: "evenodd", 
            d: "M10 18a8 8 0 100-16 8 8 0 000 16zM8.707 7.293a1 1 0 00-1.414 1.414L8.586 10l-1.293 1.293a1 1 0 101.414 1.414L10 11.414l1.293 1.293a1 1 0 001.414-1.414L11.414 10l1.293-1.293a1 1 0 00-1.414-1.414L10 8.586 8.707 7.293z", 
            clipRule: "evenodd" 
          })
        ),
        React.createElement('div', null,
          React.createElement('p', { className: "font-bold" }, "Error"),
          React.createElement('p', null, state.notification.message)
        )
      )
    )
  );

  return React.createElement('div', { className: "min-h-screen bg-gray-50" },
    // Notification
    state.notification?.type === 'success' ? React.createElement(SuccessNotification) : 
    state.notification?.type === 'error' ? React.createElement(ErrorNotification) : null,

    // Header
    React.createElement('header', { className: "bg-white shadow-sm" },
      React.createElement('div', { className: "max-w-7xl mx-auto px-4 sm:px-6 lg:px-8" },
        React.createElement('div', { className: "flex justify-between h-16 items-center" },
          React.createElement('div', { className: "flex items-center" },
            React.createElement('div', { className: "flex-shrink-0 flex items-center" },
              React.createElement('span', { className: "text-2xl" }, "🏪"),
              React.createElement('h1', { className: "ml-2 text-xl font-bold text-gray-900" }, "Walmart Rewards+ Admin")
            )
          ),
          React.createElement('div', { className: "flex items-center" },
            React.createElement('div', { className: "ml-4 flex items-center md:ml-6 relative" },
              React.createElement('button', {
                className: "flex items-center space-x-2 focus:outline-none",
                onClick: () => setState(prev => ({ ...prev, showProfileMenu: !prev.showProfileMenu }))
              },
                React.createElement('div', { className: "h-8 w-8 rounded-full bg-blue-500 flex items-center justify-center text-white font-medium" }, "AK"),
                React.createElement('span', { className: "hidden md:inline text-sm font-medium" }, "Admin Kumar")
              ),
              state.showProfileMenu && (
                React.createElement('div', {
                  ref: profileMenuRef,
                  className: "absolute right-0 mt-2 w-48 bg-white rounded-md shadow-lg py-1 z-50"
                },
                  React.createElement('div', { className: "px-4 py-2 text-sm text-gray-700 border-b" },
                    React.createElement('p', { className: "font-medium" }, "Admin Kumar"),
                    React.createElement('p', { className: "text-gray-500 text-xs" }, "admin.kumar@walmart.com")
                  ),
                  React.createElement('button', {
                    className: "block w-full text-left px-4 py-2 text-sm text-gray-700 hover:bg-gray-100",
                    onClick: () => {
                      setState(prev => ({
                        ...prev,
                        notification: {
                          type: 'success',
                          message: 'You have been logged out successfully'
                        },
                        showProfileMenu: false
                      }));
                      setTimeout(() => setState(prev => ({ ...prev, notification: null })), 3000);
                    }
                  }, "Sign out")
                )
              )
            )
          )
        )
      )
    ),

    // Main Content
    React.createElement('main', { className: "max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6" },
      React.createElement('div', { className: "flex flex-col lg:flex-row gap-6" },
        // Left Column - Transactions
        React.createElement('div', { className: "lg:w-1/2" },
          // Customer Search
          React.createElement('div', { className: "bg-white rounded-xl shadow-md p-6 mb-6" },
            React.createElement('h2', { className: "text-lg font-bold text-gray-800 mb-4" }, "🔍 Customer Search"),
            React.createElement('form', { onSubmit: handleSearch, className: "mb-4" },
              React.createElement('div', { className: "flex" },
                React.createElement('input', {
                  type: "text",
                  value: state.searchQuery,
                  onChange: (e) => setState(prev => ({ ...prev, searchQuery: e.target.value })),
                  className: "flex-1 px-4 py-2 border border-gray-300 rounded-l-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500",
                  placeholder: "Search by ID, name, phone or email"
                }),
                React.createElement('button', {
                  type: "submit",
                  className: "px-4 py-2 bg-blue-600 text-white rounded-r-lg hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500"
                }, "Search")
              )
            ),

            state.searchResults.length > 0 ? (
              React.createElement('div', { className: "border border-gray-200 rounded-lg overflow-hidden" },
                state.searchResults.map((customer) => (
                  React.createElement('div', {
                    key: customer.id,
                    onClick: () => {
                      setState(prev => ({
                        ...prev,
                        selectedCustomer: customer,
                        selectedTier: customer.currentTier,
                        searchResults: []
                      }));
                    },
                    className: `p-3 border-b border-gray-200 hover:bg-gray-50 cursor-pointer transition-colors ${
                      state.selectedCustomer?.id === customer.id ? 'bg-blue-50' : ''
                    }`
                  },
                    React.createElement('div', { className: "flex justify-between items-center" },
                      React.createElement('div', null,
                        React.createElement('p', { className: "font-medium" }, customer.name),
                        React.createElement('p', { className: "text-sm text-gray-600" }, `ID: ${customer.id}`)
                      ),
                      React.createElement('div', { className: "text-right" },
                        React.createElement('p', { className: `text-sm ${tierBenefits[customer.currentTier].color}` },
                          `${customer.currentTier} Tier`
