              React.createElement('p', { className: "text-sm text-gray-500" }, "No customers found")
            ) : null
          ),

          // Recent Transactions
          React.createElement('div', { className: "bg-white rounded-xl shadow-md p-6" },
            React.createElement('div', { className: "flex justify-between items-center mb-4" },
              React.createElement('h2', { className: "text-lg font-bold text-gray-800" }, "📝 Recent Transactions"),
              React.createElement('button', {
                onClick: () => setState(prev => ({ ...prev, activeModal: 'addPoints' })),
                disabled: !state.selectedCustomer,
                className: `px-4 py-2 rounded-lg font-medium transition-all ${
                  state.selectedCustomer 
                    ? 'bg-gradient-to-r from-green-600 to-blue-600 text-white hover:from-green-700 hover:to-blue-700 shadow-md'
                    : 'bg-gray-200 text-gray-500 cursor-not-allowed'
                }`
              }, "Add Points")
            ),

            React.createElement('div', { className: "overflow-x-auto" },
              React.createElement('table', { className: "w-full" },
                React.createElement('thead', null,
                  React.createElement('tr', { className: "border-b border-gray-200" },
                    React.createElement('th', { className: "text-left py-3 text-gray-600 font-medium text-sm" }, "Date"),
                    React.createElement('th', { className: "text-left py-3 text-gray-600 font-medium text-sm" }, "Customer"),
                    React.createElement('th', { className: "text-left py-3 text-gray-600 font-medium text-sm" }, "Amount"),
                    React.createElement('th', { className: "text-left py-3 text-gray-600 font-medium text-sm" }, "Points"),
                    React.createElement('th', { className: "text-left py-3 text-gray-600 font-medium text-sm" }, "Tier")
                  )
                ),
                React.createElement('tbody', null,
                  recentTransactions.map((txn, index) => (
                    React.createElement('tr', {
                      key: index,
                      className: `border-b border-gray-100 hover:bg-gray-50 transition-colors ${
                        state.selectedCustomer?.id === txn.customerId ? 'bg-blue-50' : ''
                      }`,
                      onClick: () => {
                        const customer = sampleCustomers.find(c => c.id === txn.customerId);
                        if (customer) {
                          setState(prev => ({
                            ...prev,
                            selectedCustomer: customer,
                            selectedTier: customer.currentTier
                          }));
                        }
                      }
                    },
                      React.createElement('td', { className: "py-3 text-gray-800 text-sm" }, txn.date.split(' ')[0]),
                      React.createElement('td', { className: "py-3 text-gray-800 text-sm" }, txn.customerName),
                      React.createElement('td', { className: "py-3 text-gray-800 text-sm" }, formatCurrency(txn.amount)),
                      React.createElement('td', { className: "py-3 text-green-600 text-sm font-medium" }, `+${txn.points}`),
                      React.createElement('td', { className: "py-3 text-sm" },
                        React.createElement('span', {
                          className: `inline-flex items-center px-2 py-0.5 rounded text-xs font-medium ${tierBenefits[txn.tier].bgColor} ${tierBenefits[txn.tier].color}`
                        }, txn.tier)
                      )
                    )
                  ))
                )
              )
            )
          )
        ),

        // Right Column - Customer Details
        React.createElement('div', { className: "lg:w-1/2" },
          state.selectedCustomer ? (
            React.createElement('div', { className: "bg-white rounded-xl shadow-md p-6 sticky top-6" },
              React.createElement('div', { className: "flex justify-between items-start mb-6" },
                React.createElement('div', null,
                  React.createElement('h2', { className: "text-xl font-bold text-gray-800" }, state.selectedCustomer.name),
                  React.createElement('p', { className: "text-sm text-gray-600" }, `Customer ID: ${state.selectedCustomer.id}`)
                ),
                React.createElement('div', { className: "flex items-center space-x-2" },
                  React.createElement('span', { className: "text-2xl" }, tierBenefits[state.selectedCustomer.currentTier].icon),
                  React.createElement('span', {
                    className: `font-bold ${tierBenefits[state.selectedCustomer.currentTier].color}`
                  }, state.selectedCustomer.currentTier)
                )
              ),

              React.createElement('div', { className: "grid grid-cols-2 gap-4 mb-6" },
                React.createElement('div', { className: "bg-gray-50 p-4 rounded-lg" },
                  React.createElement('p', { className: "text-sm text-gray-500" }, "Total Points"),
                  React.createElement('p', { className: "text-2xl font-bold text-blue-600" },
                    state.selectedCustomer.totalPoints.toLocaleString()
                  )
                ),
                React.createElement('div', { className: "bg-gray-50 p-4 rounded-lg" },
                  React.createElement('p', { className: "text-sm text-gray-500" }, "Last Purchase"),
                  React.createElement('p', { className: "text-xl font-medium text-gray-800" },
                    state.selectedCustomer.lastPurchase
                  )
                )
              ),

              React.createElement('div', { className: "mb-6" },
                React.createElement('h3', { className: "font-bold text-gray-800 mb-2" }, "Contact Information"),
                React.createElement('div', { className: "grid grid-cols-2 gap-4 text-sm" },
                  React.createElement('div', null,
                    React.createElement('p', { className: "text-gray-500" }, "Phone"),
                    React.createElement('p', { className: "text-gray-800" }, state.selectedCustomer.phone)
                  ),
                  React.createElement('div', null,
                    React.createElement('p', { className: "text-gray-500" }, "Email"),
                    React.createElement('p', { className: "text-gray-800" }, state.selectedCustomer.email)
                  )
                )
              ),

              React.createElement('div', { className: "mb-6" },
                React.createElement('h3', { className: "font-bold text-gray-800 mb-2" }, "Tier Benefits"),
                React.createElement('div', {
                  className: `p-4 rounded-lg ${tierBenefits[state.selectedCustomer.currentTier].bgColor}`
                },
                  React.createElement('ul', { className: "list-disc list-inside space-y-1 text-sm" },
                    tierBenefits[state.selectedCustomer.currentTier].features.map((feature, index) => (
                      React.createElement('li', {
                        key: index,
                        className: tierBenefits[state.selectedCustomer.currentTier].color
                      }, feature)
                    ))
                  )
                )
              ),

              React.createElement('div', null,
                React.createElement('h3', { className: "font-bold text-gray-800 mb-2" }, "Points Activity"),
                React.createElement('div', { className: "bg-gray-50 rounded-lg p-4" },
                  React.createElement('div', { className: "h-64 overflow-y-auto" },
                    recentTransactions
                      .filter(txn => txn.customerId === state.selectedCustomer.id)
                      .sort((a, b) => new Date(b.date) - new Date(a.date))
                      .map((txn, index) => (
                        React.createElement('div', {
                          key: index,
                          className: "mb-3 pb-3 border-b border-gray-200 last:border-0 last:mb-0 last:pb-0"
                        },
                          React.createElement('div', { className: "flex justify-between" },
                            React.createElement('div', null,
                              React.createElement('p', { className: "font-medium text-gray-800" }, txn.date.split(' ')[0]),
                              React.createElement('p', { className: "text-xs text-gray-500" }, `TXN: ${txn.id}`)
                            ),
                            React.createElement('div', { className: "text-right" },
                              React.createElement('p', { className: "text-gray-800" }, formatCurrency(txn.amount)),
                              React.createElement('p', { className: "text-green-600 font-medium" }, `+${txn.points} pts`)
                            )
                          )
                        )
                      ))
                  )
                )
              )
            )
          ) : (
            React.createElement('div', { className: "bg-white rounded-xl shadow-md p-6 h-full flex items-center justify-center" },
              React.createElement('div', { className: "text-center" },
                React.createElement('div', { className: "mx-auto flex items-center justify-center h-12 w-12 rounded-full bg-gray-100 mb-4" },
                  React.createElement('span', { className: "text-gray-400 text-xl" }, "👤")
                ),
                React.createElement('h3', { className: "text-lg font-medium text-gray-900 mb-1" }, "No customer selected"),
                React.createElement('p', { className: "text-gray-500" }, "Search for a customer to view their details")
              )
            )
          )
        )
      )
    ),

    // Add Points Modal
    state.activeModal === 'addPoints' && React.createElement(Modal, {
      title: "➕ Add Points to Customer",
      onClose: () => setState(prev => ({ ...prev, activeModal: null }))
    }, React.createElement(AddPointsModal))
  );
};

export default WalmartRewardsAdmin;
