import React, { useState, useRef, useEffect } from 'react';

const WalmartRewardsAdmin = () => {
  // State management
  const [state, setState] = useState({
    activeModal: null,
    purchaseAmount: '',
    selectedTier: 'Gold',
    searchQuery: '',
    searchResults: [],
    selectedCustomer: null,
    showProfileMenu: false,
    transactionStatus: null,
    notification: null
  });

  // Refs
  const profileMenuRef = useRef();
  const modalRef = useRef();

  // Sample data
  const sampleCustomers = [
    {
      id: "CUST1001",
      name: "Rajesh Kumar",
      currentTier: "Gold",
      totalPoints: 23450,
      phone: "+919876543210",
      email: "rajesh.kumar@example.com",
      lastPurchase: "2025-07-06",
      joinDate: "2023-05-15"
    },
    {
      id: "CUST1002",
      name: "Priya Sharma",
      currentTier: "Silver",
      totalPoints: 12500,
      phone: "+919876543211",
      email: "priya.sharma@example.com",
      lastPurchase: "2025-07-05",
      joinDate: "2024-01-22"
    },
    {
      id: "CUST1003",
      name: "Amit Patel",
      currentTier: "Platinum",
      totalPoints: 45600,
      phone: "+919876543212",
      email: "amit.patel@example.com",
      lastPurchase: "2025-07-07",
      joinDate: "2022-11-30"
    },
    {
      id: "CUST1004",
      name: "Kavya Iyer",
      currentTier: "Gold",
      totalPoints: 18920,
      phone: "+919876543213",
      email: "kavya.iyer@example.com",
      lastPurchase: "2025-07-04",
      joinDate: "2023-08-22"
    },
    {
      id: "CUST1005",
      name: "Rohit Singh",
      currentTier: "Silver",
      totalPoints: 8750,
      phone: "+919876543214",
      email: "rohit.singh@example.com",
      lastPurchase: "2025-07-08",
      joinDate: "2024-03-15"
    }
  ];

  const tierBenefits = {
    Silver: {
      icon: "ðŸ¥ˆ",
      color: "text-slate-600",
      bgColor: "bg-slate-100",
      borderColor: "border-slate-200",
      purchaseRate: 0.15,
      features: ["15% points on purchases", "1.2x bonus on recycling"]
    },
    Gold: {
      icon: "ðŸ¥‡",
      color: "text-amber-600",
      bgColor: "bg-amber-100",
      borderColor: "border-amber-200",
      purchaseRate: 0.18,
      features: ["18% points on purchases", "1.3x recycling bonus", "Faster redemption"]
    },
    Platinum: {
      icon: "ðŸ’Ž",
      color: "text-purple-600",
      bgColor: "bg-purple-100",
      borderColor: "border-purple-200",
      purchaseRate: 0.20,
      features: ["20% points on purchases", "1.5x recycling", "Exclusive partners unlocked"]
    }
  };

  const recentTransactions = [
    { 
      id: "TXN78901",
      customerId: "CUST1001",
      customerName: "Rajesh Kumar",
      date: "2025-07-06 14:30", 
      amount: 4299, 
      points: 645,
      tier: "Gold",
      cashier: "Admin001"
    },
    { 
      id: "TXN78902",
      customerId: "CUST1002",
      customerName: "Priya Sharma",
      date: "2025-07-05 11:15", 
      amount: 1150, 
      points: 173,
      tier: "Silver",
      cashier: "Admin002"
    },
    { 
      id: "TXN78903",
      customerId: "CUST1003",
      customerName: "Amit Patel",
      date: "2025-07-07 16:45", 
      amount: 2990, 
      points: 598,
      tier: "Platinum",
      cashier: "Admin001"
    },
    { 
      id: "TXN78904",
      customerId: "CUST1001",
      customerName: "Rajesh Kumar",
      date: "2025-6-29 09:20", 
      amount: 1150, 
      points: 173,
      tier: "Gold",
      cashier: "Admin003"
    },
    { 
      id: "TXN78905",
      customerId: "CUST1004",
      customerName: "Kavya Iyer",
      date: "2025-07-04 16:20", 
      amount: 2750, 
      points: 495,
      tier: "Gold",
      cashier: "Admin001"
    }
  ];

  // Click outside handler for profile menu
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (profileMenuRef.current && !profileMenuRef.current.contains(event.target)) {
        setState(prev => ({ ...prev, showProfileMenu: false }));
      }
    };
    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  // Handle search with proper filtering
  const handleSearch = (e) => {
    e.preventDefault();
    const query = state.searchQuery.trim().toLowerCase();

    if (!query) {
      setState(prev => ({ ...prev, searchResults: [] }));
      return;
    }

    const results = sampleCustomers.filter(customer => 
      customer.id.toLowerCase().includes(query) || 
      customer.name.toLowerCase().includes(query) ||
      customer.phone.includes(query) ||
      customer.email.toLowerCase().includes(query)
    );

    setState(prev => ({ ...prev, searchResults: results }));
  };

  // Real-time search as user types
  useEffect(() => {
