import React, { useState } from 'react';

const WalmartRewardsApp = () => {
  const [activeModal, setActiveModal] = useState(null);
  const [selectedConversion, setSelectedConversion] = useState(null);
  const [conversionAmount, setConversionAmount] = useState('');
  const [currentPoints, setCurrentPoints] = useState(23450);
  const [showSuccess, setShowSuccess] = useState(false);
  const [lastConversion, setLastConversion] = useState(null);

  const customerData = {
    name: "Rajesh Kumar",
    currentTier: "Gold",
    totalPoints: currentPoints,
    pointsToNextTier: 6550,
    tierProgress: 78
  };

  const tierBenefits = {
    Silver: {
      icon: "ðŸ¥ˆ",
      color: "text-gray-600",
      bgColor: "bg-gray-100",
      purchaseBonus: "15%",
      recyclingBonus: "1.2x",
      features: ["15% points on purchases", "1.2x bonus on recycling"]
    },
    Gold: {
      icon: "ðŸ¥‡",
      color: "text-yellow-600",
      bgColor: "bg-yellow-100",
      purchaseBonus: "18%",
      recyclingBonus: "1.3x",
      features: ["18% points on purchases", "1.3x recycling bonus", "Faster redemption"]
    },
    Platinum: {
      icon: "ðŸ’Ž",
      color: "text-purple-600",
      bgColor: "bg-purple-100",
      purchaseBonus: "20%",
      recyclingBonus: "1.5x",
      features: ["20% points on purchases", "1.5x recycling", "Exclusive partners unlocked"]
    }
  };

  const recentPurchases = [
    { date: "July 6, 2025", amount: 4299, invoiceId: "WAL-INV-34213", points: 645 },
    { date: "June 29, 2025", amount: 1150, invoiceId: "WAL-INV-33782", points: 173 },
    { date: "June 22, 2025", amount: 2990, invoiceId: "WAL-INV-33409", points: 448 },
    { date: "June 15, 2025", amount: 3450, invoiceId: "WAL-INV-33201", points: 518 },
    { date: "June 8, 2025", amount: 2100, invoiceId: "WAL-INV-32987", points: 315 }
  ];

  const pointsHistory = [
    { date: "July 6", action: "Purchase", item: "Kitchen Blender", points: 645, bonus: "+15%", type: "purchase" },
    { date: "July 4", action: "Recycle", item: "Old Smartphone", points: 750, bonus: "+30%", type: "recycle" },
    { date: "June 29", action: "Return", item: "Shoes", points: -260, bonus: "(-1.5x rule)", type: "return" },
    { date: "June 25", action: "Purchase", item: "Groceries", points: 312, bonus: "+15%", type: "purchase" }
  ];

  const rewardOptions = [
    {
      id: 'airmiles',
      icon: "âœˆï¸",
      title: "Air Miles",
      conversionRate: 5,
      unit: "miles",
      minPoints: 5000,
      notes: "Valid on Indigo, Vistara",
      bgColor: "bg-blue-50",
      borderColor: "border-blue-200"
    },
    {
      id: 'hotel',
      icon: "ðŸ¨",
      title: "Hotel Points",
      conversionRate: 4,
      unit: "hotel points",
      minPoints: 10000,
      notes: "Only on weekdays/off-season",
      bgColor: "bg-green-50",
      borderColor: "border-green-200"
    },
    {
      id: 'coupon',
      icon: "ðŸŽŸï¸",
      title: "Walmart Coupon",
      conversionRate: 10,
      unit: "â‚¹",
      minPoints: 2500,
      notes: "(min cart â‚¹2,000) Limit: 2 per month",
      bgColor: "bg-orange-50",
      borderColor: "border-orange-200"
    },
    {
      id: 'cashback',
      icon: "ðŸ’°",
      title: "Cashback",
      conversionRate: 20,
      unit: "â‚¹",
      minPoints: 5000,
      notes: "Direct bank transfer",
      bgColor: "bg-purple-50",
      borderColor: "border-purple-200"
    }
  ];

  const pointsSummary = {
    shopping: 3200,
    recycling: 1850,
    returns: -390,
    net: 4660
  };

  const calculateConversion = (points, option) => {
    if (!points || !option) return 0;
    return Math.floor(points / option.conversionRate);
  };

  const handleConversion = () => {
    if (!selectedConversion) return;

    const pointsToConvert = parseInt(conversionAmount);
    const option = rewardOptions.find(opt => opt.id === selectedConversion);

    if (pointsToConvert < option.minPoints) {
      alert(`Minimum ${option.minPoints} points required for ${option.title}`);
      return;
    }
    if (pointsToConvert > currentPoints) {
      alert('Insufficient points balance');
      return;
    }

    const convertedValue = calculateConversion(pointsToConvert, option);

    setCurrentPoints(prev => prev - pointsToConvert);
    setLastConversion({
      option: option,
      points: pointsToConvert,
      value: convertedValue
    });
    setShowSuccess(true);
    setConversionAmount('');
    setSelectedConversion(null);

    setTimeout(() => setShowSuccess(false), 5000);
  };

  const Modal = ({ title, children, onClose }) => (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-xl max-w-2xl w-full max-h-[90vh] overflow-y-auto shadow-2xl">
        <div className="p-6 border-b border-gray-200 flex justify-between items-center">
          <h2 className="text-xl font-bold text-gray-800">{title}</h2>
          <button
            onClick={onClose}
            className="text-gray-500 hover:text-gray-700 text-2xl w-8 h-8 rounded-full hover:bg-gray-100 flex items-center justify-center transition-colors"
          >
            Ã—
          </button>
        </div>
        <div className="p-6">
          {children}
        </div>
      </div>
    </div>
  );

  const SuccessModal = () => (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-2xl max-w-md w-full shadow-2xl">
        <div className="p-8 text-center">
          <div className="w-16 h-16 bg-green-100 rounded-full flex items-center justify-center mx-auto mb-4">
            <svg className="w-8 h-8 text-green-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 13l4 4L19 7" />
            </svg>
          </div>
          <h3 className="text-xl font-bold text-gray-800 mb-2">Conversion Successful!</h3>
          <p className="text-gray-600 mb-4">
            Your {lastConversion?.points.toLocaleString()} EcoPoints have been converted to{' '}
            <span className="font-bold text-blue-600">
              {lastConversion?.value} {lastConversion?.option.unit}
            </span>
          </p>
          <div className="bg-blue-50 p-4 rounded-lg mb-4">
            <p className="text-sm text-blue-800">
              ðŸŽ‰ {lastConversion?.option.title} will be processed within 24 hours
            </p>
          </div>
          <button
            onClick={() => setShowSuccess(false)}
            className="w-full bg-gradient-to-r from-blue-600 to-purple-600 text-white py-3 px-6 rounded-lg font-medium hover:from-blue-700 hover:to-purple-700 transition-all duration-200"
          >
            Continue Shopping
          </button>
        </div>
      </div>
    </div>
  );

  const getTypeIcon = (type) => {
    switch(type) {
      case 'purchase': return 'ðŸ›’';
      case 'recycle': return 'â™»ï¸';
      case 'return': return 'â†©ï¸';
      default: return 'ðŸ“„';
    }
  };

  const getTypeColor = (type) => {
    switch(type) {
      case 'purchase': return 'bg-green-100 text-green-600';
      case 'recycle': return 'bg-blue-100 text-blue-600';
      case 'return': return 'bg-red-100 text-red-600';
      default: return 'bg-gray-100 text-gray-600';
    }
  };

  const PointsHistoryModal = () => (
    <div className="space-y-4">
      {pointsHistory.map((entry, index) => (
        <div key={index} className="flex items-center justify-between p-4 bg-gray-50 rounded-lg hover:bg-gray-100 transition-colors">
          <div className="flex items-center space-x-4">
            <div className={`p-2 rounded-full ${getTypeColor(entry.type)}`}>
              <span className="text-lg">{getTypeIcon(entry.type)}</span>
            </div>
            <div>
              <p className="font-medium text-gray-800">{entry.date} - {entry.action}</p>
              <p className="text-sm text-gray-600">{entry.item}</p>
            </div>
          </div>
          <div className="text-right">
            <p className={`font-bold ${entry.points > 0 ? 'text-green-600' : 'text-red-600'}`}>
              {entry.points > 0 ? '+' : ''}{entry.points} pts
            </p>
            <p className="text-xs text-gray-500">{entry.bonus}</p>
          </div>
        </div>
      ))}

      <div className="mt-6 p-4 bg-gradient-to-r from-blue-50 to-purple-50 rounded-lg border border-blue-200">
        <h3 className="font-bold text-blue-800 mb-2">ðŸ“Š Points Summary</h3>
        <div className="grid grid-cols-2 gap-4 text-sm">
          <div>
            <p className="text-gray-600">Shopping Points: <span className="font-medium text-green-600">+{pointsSummary.shopping} pts</span></p>
            <p className="text-gray-600">Recycling Points: <span className="font-medium text-blue-600">+{pointsSummary.recycling} pts</span></p>
          </div>
          <div>
            <p className="text-gray-600">Return Deductions: <span className="font-medium text-red-600">{pointsSummary.returns} pts</span></p>
            <p className="text-gray-600 font-bold">Net Points: <span className="text-purple-600">{pointsSummary.net} EcoPoints</span></p>
          </div>
        </div>
      </div>
    </div>
  );

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-purple-50">
      {/* Header */}
      <div className="bg-gradient-to-r from-blue-600 to-purple-600 text-white p-6 shadow-lg">
        <div className="max-w-7xl mx-auto">
          <div className="flex items-center justify-between">
            <div>
              <h1 className="text-3xl font-bold mb-1">ðŸª Walmart Rewards+</h1>
            </div>
            <div className="text-right">
              <p className="text-sm text-blue-100">Tier: {tierBenefits[customerData.currentTier].icon} {customerData.currentTier}</p>
              <p className="text-xl font-bold">Total Points: {currentPoints.toLocaleString()} EcoPoints</p>
            </div>
          </div>
        </div>
      </div>

      {/* Welcome Message */}
      <div className="bg-white shadow-sm border-b border-gray-200">
        <div className="max-w-7xl mx-auto px-6 py-4">
          <p className="text-gray-700 text-lg">Welcome back, <span className="font-semibold text-blue-600">{customerData.name}</span>!</p>
        </div>
      </div>

      {/* Main Content Container */}
      <div className="max-w-7xl mx-auto p-6">
        {/* Grid Layout with consistent gaps */}
        <div className="grid grid-cols-1 lg:grid-cols-12 gap-6">

          {/* Recent Purchases - 8 columns */}
          <div className="lg:col-span-8">
            <div className="bg-white rounded-2xl shadow-lg p-6 border border-gray-100 h-full">
              <h2 className="text-xl font-bold text-gray-800 mb-4">ðŸ“„ Recent Purchases</h2>
              <div className="overflow-x-auto">
                <table className="w-full">
                  <thead>
                    <tr className="border-b border-gray-200">
                      <th className="text-left py-3 text-gray-600 font-medium">Date</th>
                      <th className="text-left py-3 text-gray-600 font-medium">Amount</th>
                      <th className="text-left py-3 text-gray-600 font-medium">Invoice ID</th>
                      <th className="text-left py-3 text-gray-600 font-medium">Points</th>
                      <th className="text-left py-3 text-gray-600 font-medium">Action</th>
                    </tr>
                  </thead>
                  <tbody>
                    {recentPurchases.map((purchase, index) => (
                      <tr key={index} className="border-b border-gray-100 hover:bg-gray-50 transition-colors">
                        <td className="py-3 text-gray-800 text-sm">{purchase.date}</td>
                        <td className="py-3 text-gray-800 font-medium">â‚¹{purchase.amount.toLocaleString()}</td>
                        <td className="py-3 text-gray-600 text-sm">{purchase.invoiceId}</td>
                        <td className="py-3 text-green-600 font-medium">+{purchase.points}</td>
                        <td className="py-3">
                          <button className="text-blue-600 hover:text-blue-800 flex items-center space-x-1 transition-colors text-sm">
                            <span>ðŸ“¥</span>
                            <span>PDF</span>
                          </button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
              <button
                onClick={() => setActiveModal('history')}
                className="mt-4 text-blue-600 hover:text-blue-800 flex items-center space-x-1 transition-colors"
              >
                <span>ðŸ‘ï¸</span>
                <span>View Full Points History</span>
                <span>â†’</span>
              </button>
            </div>
          </div>

          {/* Tier & Points Summary - 4 columns */}
          <div className="lg:col-span-4">
            <div className="bg-white rounded-2xl shadow-lg p-6 border border-gray-100 h-full">
              <h2 className="text-xl font-bold text-gray-800 mb-4">ðŸ’¼ Tier & Points Summary</h2>

              <div className="space-y-3 mb-6">
                {Object.entries(tierBenefits).map(([tier, details]) => (
                  <div key={tier} className={`p-3 rounded-xl border-2 transition-all duration-200 ${
                    tier === customerData.currentTier
                      ? 'border-blue-500 bg-gradient-to-br from-blue-50 to-purple-50 shadow-md'
                      : 'border-gray-200 hover:border-gray-300'
                  }`}>
                    <div className="flex items-center space-x-2 mb-2">
                      <span className="text-xl">{details.icon}</span>
                      <h3 className={`font-bold ${details.color} text-sm`}>{tier}</h3>
                    </div>
                    <div className="text-xs text-gray-600 space-y-1">
                      {details.features.map((feature, index) => (
                        <p key={index}>â€¢ {feature}</p>
                      ))}
                    </div>
                  </div>
                ))}
              </div>

              <div className="bg-gradient-to-r from-yellow-50 to-purple-50 p-4 rounded-xl border border-yellow-200">
                <p className="text-gray-700 mb-2 text-sm">
                  You are currently <span className="font-bold text-yellow-600">{customerData.currentTier} Tier</span>.
                  Only <span className="font-bold text-purple-600">{customerData.pointsToNextTier.toLocaleString()} points</span> left to reach Platinum.
                </p>
                <div className="w-full bg-gray-200 rounded-full h-2 mb-2">
                  <div
                    className="bg-gradient-to-r from-yellow-500 to-purple-500 h-2 rounded-full transition-all duration-500"
                    style={{ width: `${customerData.tierProgress}%` }}
                  ></div>
                </div>
                <p className="text-xs text-gray-600">{customerData.tierProgress}% to Platinum</p>
              </div>
            </div>
          </div>
        </div>

        {/* Point Conversion System - Full width with consistent gap */}
        <div className="mt-6">
          <div className="bg-white rounded-2xl shadow-lg p-6 border border-gray-100">
            <h2 className="text-xl font-bold text-gray-800 mb-6">ðŸŽ¯ Point Conversion System</h2>

            {/* Step 1: Select Conversion Type */}
            <div className="mb-6">
              <h3 className="font-semibold text-gray-700 mb-4">Step 1: Select what you want to convert to</h3>
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
                {rewardOptions.map((option) => (
                  <div
                    key={option.id}
                    className={`p-4 rounded-xl border-2 cursor-pointer transition-all duration-200 hover:shadow-md ${
                      selectedConversion === option.id
                        ? 'border-blue-500 bg-blue-50 shadow-lg'
                        : `${option.borderColor} ${option.bgColor}`
                    }`}
                    onClick={() => setSelectedConversion(option.id)}
                  >
                    <div className="flex items-center space-x-3 mb-3">
                      <span className="text-2xl">{option.icon}</span>
                      <h4 className="font-bold text-gray-800">{option.title}</h4>
                    </div>

                    <div className="mb-3">
                      <p className="text-sm text-gray-600 mb-1">Conversion Rate</p>
                      <p className="text-xs text-gray-500 font-medium">{option.conversionRate} points = 1 {option.unit}</p>
                      <p className="text-xs text-gray-500 mb-2">{option.notes}</p>
                      <p className="text-xs text-orange-600 font-medium">Min: {option.minPoints} points</p>
                    </div>
                  </div>
                ))}
              </div>
            </div>

            {/* Step 2: Enter Points Amount */}
            {selectedConversion && (
              <div className="mb-6 p-4 bg-gradient-to-r from-blue-50 to-purple-50 rounded-xl border border-blue-200">
                <h3 className="font-semibold text-blue-800 mb-4">Step 2: Enter points amount</h3>
                <div className="flex items-center justify-between mb-4">
                  <div className="flex-1 max-w-md">
                    <label className="block text-sm font-medium text-gray-700 mb-2">Points to Convert</label>
                    <input
                      type="number"
                      value={conversionAmount}
                      onChange={(e) => setConversionAmount(e.target.value)}
                      placeholder="Enter points amount"
                      className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent text-lg"
                      min="0"
                      max={currentPoints}
                    />
                  </div>

                  <div className="text-right ml-6">
                    <p className="text-sm text-gray-600">Available Balance</p>
                    <p className="text-xl font-bold text-blue-600">{currentPoints.toLocaleString()} EcoPoints</p>
                  </div>
                </div>

                {conversionAmount && (
                  <div className="bg-white p-4 rounded-lg border mb-4">
                    <p className="text-sm text-gray-600 mb-2">Conversion Preview:</p>
                    <div className="flex items-center justify-between">
                      <div>
                        <p className="text-lg font-bold text-gray-800">
                          {parseInt(conversionAmount).toLocaleString()} Points
                        </p>
                        <p className="text-sm text-gray-500">Will convert to</p>
                      </div>
                      <div className="text-2xl text-gray-400">â†’</div>
                      <div className="text-right">
                        <p className="text-lg font-bold text-green-600">
                          {calculateConversion(parseInt(conversionAmount), rewardOptions.find(opt => opt.id === selectedConversion))} {rewardOptions.find(opt => opt.id === selectedConversion)?.unit}
                        </p>
                        <p className="text-sm text-gray-500">{rewardOptions.find(opt => opt.id === selectedConversion)?.title}</p>
                      </div>
                    </div>
                  </div>
                )}

                <button
                  onClick={handleConversion}
                  disabled={!conversionAmount || parseInt(conversionAmount) < rewardOptions.find(opt => opt.id === selectedConversion)?.minPoints || parseInt(conversionAmount) > currentPoints}
                  className={`w-full py-3 px-6 rounded-lg font-medium transition-all duration-200 ${
                    conversionAmount && parseInt(conversionAmount) >= rewardOptions.find(opt => opt.id === selectedConversion)?.minPoints && parseInt(conversionAmount) <= currentPoints
                      ? 'bg-gradient-to-r from-blue-600 to-purple-600 text-white hover:from-blue-700 hover:to-purple-700 shadow-lg hover:shadow-xl'
                      : 'bg-gray-300 text-gray-500 cursor-not-allowed'
                  }`}
                >
                  {!conversionAmount ? 'Enter Points Amount' :
                   parseInt(conversionAmount) < rewardOptions.find(opt => opt.id === selectedConversion)?.minPoints ? `Minimum ${rewardOptions.find(opt => opt.id === selectedConversion)?.minPoints} points required` :
                   parseInt(conversionAmount) > currentPoints ? 'Insufficient Points' :
                   'Convert Points'}
                </button>
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Modals */}
      {activeModal === 'history' && (
        <Modal title="Points History" onClose={() => setActiveModal(null)}>
          <PointsHistoryModal />
        </Modal>
      )}

      {showSuccess && <SuccessModal />}
    </div>
  );
};

export default WalmartRewardsApp;
